import { Component } from '@angular/core';
import { ViewportScroller } from "@angular/common";
@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  constructor(private scroller: ViewportScroller) {}
  Home() {
    this.scroller.scrollToAnchor("Home");
  }
  Purpose() {
    this.scroller.scrollToAnchor("Purpose");
  }
  Standard() {
    this.scroller.scrollToAnchor("Standard");
  }
  Customer() {
    this.scroller.scrollToAnchor("Customer");
  }
  Comment() {
    this.scroller.scrollToAnchor("Comment");
  }
  Contact() {
    this.scroller.scrollToAnchor("Contact");
  }
  smoothScrollTo(anchor: string) {
    const element = document.getElementById(anchor);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start', inline: 'nearest' });
    }
  }
}
<header class="bg-white" >
   <nav class= "flex items-center h-[110px] pt-[17px] pb-[13px] pl-[103px] max-lg:px-5   max-lg:justify-center">
    <a routerLink="">
      <div class="w-[155px] h-[110]">
        <img src = "../../assets/Image/Logo.png" class="object-cover">
      </div>
    </a>
    <div class="right-0 text-black-1 w-full flex justify-end">
      <div class="menu" (click)="smoothScrollTo('Home')">หน้าหลัก</div>
      <div class="menu" (click)="smoothScrollTo('Purpose')">เป้าหมายขององค์กร</div>
      <div class="menu" (click)="smoothScrollTo('Standard')">มาตรฐานของเรา</div>
      <div class="menu" (click)="smoothScrollTo('Customer')">ลูกค้าของเรา</div>
      <div class="menu" (click)="smoothScrollTo('Comment')">Comment</div>
      <div class="menu" (click)="smoothScrollTo('Contact')">Contact us</div>
    </div>
   </nav>
</header>
