import { Component, OnInit } from '@angular/core';
import { Hero } from 'src/app/hero_type';
import { HttpClient } from '@angular/common/http';
import { DomSanitizer,SafeHtml } from '@angular/platform-browser';

@Component({
  selector: 'app-hero-section',
  templateUrl: './hero-section.component.html',
  styleUrls: ['./hero-section.component.css']
})
export class HeroSectionComponent implements OnInit  {
  heroes: any[] = [];
  imtemp:any;
  fetchedText: string = "";
  sanitizedText!: SafeHtml;
  constructor(private http: HttpClient, private sanitizer: DomSanitizer) { }

  ngOnInit():void {
    this.http.get<any[]>('http://localhost:5000/addProfiles')
    .subscribe(response => {
      this.heroes = response;
      this.fetchedText = this.heroes[0].descript;
      this.sanitizedText = this.sanitizer.bypassSecurityTrustHtml(this.fetchedText);
    })
  }
}
<div class="flex justify-center px-[150px]  py-12  max-lg:px-[16px]  max-lg:py-0">

  <div class="flex flex-col">
      <!-- Upper side  -->
      <div class="w-full flex flex-row h-2/3 pb-7 max-lg:flex-col max-lg:h-full ">
        <!-- Left side -->
        <div class="flex flex-col h-full w-1/2 pr-2 max-lg:w-full max-lg:pr-0 ">
          <!-- <div class="h-1/2"> -->
            <div class="subject ">
                <h2 class="text-[36px] text-blue-3 font-medium  max-lg:text-[18px]">{{ heroes[0].title }}</h2>
            </div>

            <div class=" text-[20px] font-light text-grey-1 w-full  max-lg:text-[16px]">
                {{ heroes[0].descript }}
            </div>
        <!-- </div> -->

          <div class="flex justify-end w-full h-full max-lg:justify-center max-lg:pt-[26px] ">
            <img routerLink="login"  [src]="heroes[0].img1" class="w-1/2 h-full cursor-pointer object-cover max-lg:w-full max-lg:py-2">
          </div>
        </div>

        <!-- Right side -->
        <div class="flex flex-row h-full w-1/2 max-lg:w-full">
          <!-- Long img -->
          <div class="h-full w-1/2 pr-2 ">
            <img [src]="heroes[0].img2" class="h-full w-full object-cover">
          </div>
          <!--  2 each -->
          <div class="h-full w-1/2 flex flex-col">
            <div class="h-1/2 pb-1">
              <img [src]="heroes[0].img3" class="h-full w-full object-cover">
            </div>
            <div class="h-1/2 pt-1">
              <img [src]="heroes[0].img4" class="h-full w-full object-cover">
            </div>
          </div>

        </div>
      </div>

      <!-- Down side -->
      <div class="flex flex-row h-1/3 w-full max-lg:flex-col max-lg:h-full max-lg:space-y-2">
        <!-- Left side -->
        <div class="w-1/2 flex flex-row h-full pr-2 max-lg:flex-col max-lg:w-full max-lg:justify-center max-lg:pr-0 max-lg:space-y-2">
          <img [src]="heroes[0].img5" class="h-full w-1/2 pr-2 object-cover max-lg:pr-0 max-lg:w-full">
          <img [src]="heroes[0].img6" class="h-full w-1/2 object-cover max-lg:w-full">
        </div>
        <!-- right side -->
        <div class="w-1/2 h-full max-lg:w-full max-lg:pb-[26px]">
          <img [src]="heroes[0].img7" class="h-full w-full object-cover">
        </div>
      </div>
  </div>

</div>
