import { Component, OnInit,HostListener  } from '@angular/core';
import { OwlOptions } from 'ngx-owl-carousel-o';
import { HttpClient } from '@angular/common/http';


@Component({
  selector: 'app-customer',
  templateUrl: './customer.component.html',
  styleUrls: ['./customer.component.css']
})
export class CustomerComponent {

  //get All Customer
  allCustomer:any[] = [];

  constructor(private http: HttpClient) { }
  ngOnInit():void {
    this.http.get<any[]>('http://localhost:5000/getCustomer')
    .subscribe(response => {
      this.allCustomer = response
      console.log(this.allCustomer)
    })

  }

  // slideShow
  customOptions: OwlOptions = {
    loop: true,
    mouseDrag: true,
    touchDrag: true,
    pullDrag: true,
    autoplay:false,
    autoplayTimeout:3000,
    dots: false,
    navSpeed: 1000,
    navText: ['', ''],
    responsive: {
      0: {
        items: 1
      },
      1024: {
        items: 6
      },

    },
    nav: false
  }

}
<div class="bg-blue-3 pt-[28px] pb-[71px] max-lg:py-[30px]" id="Customer">
    <div class="text-center">
        <div class="text-white-1 text-[44px] font-medium max-lg:text-[22px]">ลูกค้าของเรา</div>
        <div class="text-white-1 text-[30px]  max-lg:text-[16px] max-sm:text-[15px] font-light">our customer</div>
    </div>
    <div class=" pt-[28px] ">
        <owl-carousel-o [options]="customOptions" >
            <ng-template carouselSlide  >
                
            </ng-template>

          </owl-carousel-o>
    </div>
</div>
