import { Component } from '@angular/core';

@Component({
  selector: 'app-our-customer',
  templateUrl: './our-customer.component.html',
  styleUrls: ['./our-customer.component.css']
})
export class OurCustomerComponent {
  elementHTML: string = '';

  addElement() {
    this.elementHTML = `<div class="overflow-y-scroll max-h-[100%]">
      <!-- Form -->
      <div class="pt-[18px] mr-1">
        <label class="text-[0.9vw] leading-6 text-grey-5 max-lg:text-[14px]">Title (sub)</label>
        <div class="pt-[8px]">
          <input name="sub_title1" type="text" class="w-1/2 h-[51px] rounded-lg px-[20px] border-2 border-grey-4 max-lg:h-[40px] max-sm:w-full">
        </div>
      </div>
      <div class="pt-[18px] mr-1">
        <label class="text-[0.9vw] leading-6 text-grey-5 max-lg:text-[14px]">Description (sub)</label>
        <div class="pt-[8px]">
          <input name="sub_descript1" type="text" class="w-2/3 h-[51px] rounded-lg px-[20px] border-2 border-grey-4 max-lg:h-[40px] max-sm:w-full">
        </div>
      </div>
      <!-- Drag and drop -->
      <div class="pt-[40px] pr-2">
        <div class="imgs-ngx" ngx-dropzone [accept]="'image/*'" [multiple]="false">
          <img src="" class="h-full w-1/2 object-scale-down">
        </div>
      </div>
    </div>`;
  }
}
